    private void btnUpdActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnUpdActionPerformed
        
        String iden = txtBID.getText();
        CuerpoDeAgua pac = new CuerpoDeAgua();
        if (!iden.isEmpty()) {
            CuerpoDeAgua resul = pac.buscarId(iden);
            txtNom1.setText(resul.getNombre());
            txtId1.setText(String.valueOf(resul.getId()));
            txtMun1.setText(resul.getMunicipio());
            txtTc1.setText(resul.getTipoC());
            cbTa1.getSelectedItem();
            txtIrc1.setText(String.valueOf(resul.getIrca()));
            txtBID.setText("");
            
        }else{
            JOptionPane.showMessageDialog(this, "Campo de ID no debe estar vacio");
        }
    }//GEN-LAST:event_btnUpdActionPerformed

    private void btnDelActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnDelActionPerformed

        CuerpoDeAgua cda = new CuerpoDeAgua();
        String iden = txtId1.getText();
        
        cda.eliminarcda(iden);
        
        clean1();
        txtDAP.setText("");
        txtDP.setText("");
        txtBID.requestFocus();
    }//GEN-LAST:event_btnDelActionPerformed

