    private void btnProActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnProActionPerformed

        clean2();
        String rta1 = "";
        String r = "";
        int c = 0;
        String[] a = txtDAP.getText().split("\n");
        CuerpoDeAgua[] aa = new CuerpoDeAgua[a.length];

        for (int i = 0; i < a.length; i++) {

            String[] temp = a[i].split(" ");

            aa[i] = new CuerpoDeAgua(temp[0], Integer.parseInt(temp[1]), temp[2], temp[3], temp[4], Double.parseDouble(temp[5]));

        }

        String min = (aa[0].getNombre() + " " + aa[0].getId());
        double vmin = aa[0].getIrca();

        for (int i = 0; i < aa.length; i++) {
            rta1 = (rta1 + aa[i].nivel() + "\n");
            if (aa[i].getIrca() <= 35) {
                c++;
            }
            if (aa[i].nivel().equals("MEDIO")) {
                r = (r + aa[i].getNombre() + " ");
            }
            if (aa[i].getIrca() < vmin) {
                min = (aa[i].getNombre() + " " + aa[i].getId());
                vmin = aa[i].getIrca();
            }
        }

        if ("".equals(r)) {
            r = "NA";
        } else {
        }

        txtDP.setText(rta1 + c + "\n" + r + "\n" + min);
    }//GEN-LAST:event_btnProActionPerformed

    private void btnLISTARActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnLISTARActionPerformed

        txtDAP.setText("");
        CuerpoDeAgua pac = new CuerpoDeAgua();
        ArrayList<CuerpoDeAgua> vcda = pac.listarcda();

        for (CuerpoDeAgua p : vcda) {
            txtDAP.append(p.getNombre() + " " + p.getId() + " " + p.getMunicipio() + " " + p.getTipoC()+ " " + p.getTipoA() + " " + p.getIrca() + "\n");
        }
        
    }//GEN-LAST:event_btnLISTARActionPerformed

