    private void btnAgrActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAgrActionPerformed

        CuerpoDeAgua cda = new CuerpoDeAgua();

        cda.savecda(txtNom.getText(), txtId.getText(), txtMun.getText(),
                txtTc.getText(), cbTa.getSelectedItem().toString(), txtIrc.getText());

        clean();
        txtDAP.setText("");
        txtDP.setText("");
        txtNom.requestFocus();

    }//GEN-LAST:event_btnAgrActionPerformed

