    private void btnAfeActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAfeActionPerformed
        DensidadPoblacional dp = new DensidadPoblacional();

        if (!txtAfe.getText().isEmpty()) {
            dp.seth(Integer.parseInt(txtAfe.getText()));
            String nivelAfe = dp.afeccion();
            txtRA.setText(nivelAfe);
            txtAfe.setText("");
        } else {
            JOptionPane.showMessageDialog(this, "Campo Cantidad de habitantes no puede estar vacio");
        }
    }//GEN-LAST:event_btnAfeActionPerformed

